package by.epam.tr.main;

public class raznosc_x_y {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			double x;
			double y;
			
			x = 47.31;
			y = 29.89;
			
			double sum = x + y;
			double razn = y - x;
			double proizw = y * x;
			double czastn = y / x;
			
			System.out.println("sum="+ sum);
			System.out.println("razn="+ razn);
			System.out.println("proisw="+ proizw);
			System.out.println("czastn="+ czastn);
			
	}

}
